import React, { useEffect, useRef } from 'react';
import {FaLinkedin} from 'react-icons/fa';
import './Contact.css';

function Contact() {
  const contactRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          contactRef.current.classList.add('animate');
        }
      },
      { threshold: 0.1 }
    );

    if (contactRef.current) {
      observer.observe(contactRef.current);
    }

    return () => {
      if (contactRef.current) {
        observer.unobserve(contactRef.current);
      }
    };
  }, []);

  return (
    <div className="contact-container" ref={contactRef}>
      <h2 className="contact-title">Contact Me</h2>
      <div className="contact-content">
        <div className="contact-item">
          <FaEnvelope className="contact-icon" />
          <p>Email: <a href="mailto:your.email@example.com">your.email@example.com</a></p>
        </div>
        <div className="contact-item">
          <FaLinkedin className="contact-icon" />
          <p>LinkedIn: <a href="https://www.linkedin.com/in/your-profile" target="_blank" rel="noopener noreferrer">linkedin.com/in/your-profile</a></p>
        </div>
        <div className="contact-item">
          <FaGithub className="contact-icon" />
          <p>GitHub: <a href="https://github.com/your-username" target="_blank" rel="noopener noreferrer">github.com/your-username</a></p>
        </div>
      </div>
      <form className="contact-form">
        <input type="text" placeholder="Your Name" required />
        <input type="email" placeholder="Your Email" required />
        <textarea placeholder="Your Message" required></textarea>
        <button type="submit">Send Message</button>
      </form>
    </div>
  );
}

export default Contact;
